function checkEligibile(assignedTo) {
    try {
        var eligible;		
        var count = 0;
        var gr = new GlideRecord('x_801107_library_m_books');
        gr.addQuery('assigned_to', assignedTo);
        gr.query();
        while (gr.next()) {
            count += 1;
            if (roles.includes('x_801107_library_m.members_user')) {
                if (count == 10) {
                    eligible = false;
                } else {
                    eligible = true;
                }
            } else if (!roles.includes('x_801107_library_m.members_user')) {
                if (count == 5) {
                    eligible = false;
                } else {
                    eligible = true;
                }
            }
        }
		gs.addInfoMessage(eligible);
        return eligible;
    } catch (err) {
        gs.addErrorMessage("line 29: " + roles.includes('x_801107_library_m.members_user') + " " + err);
    }
}
